const app = express();
const port = 8000;



const StaticRoute = express.static('static');

app.use('/', StaticRoute);



app.get('/api/id/:userId', (req, res) => {
    const userId = req.params.userId;

    const user = getDataById(userId);

    if (user) {
        res.json(user);
    } else {
        res.json({ message: 'No data for this ID' });
    }
});

app.listen(port, () => {
    console.log(`Listening on port ${port}`);
});

